//import React, { Component } from 'react';
import React, { useState, useEffect } from "react";
export default function FetchData() {
    //const [result, setResult] = useState(null);
    const url =
      "http://api.openweathermap.org/data/2.5/weather?q=Kolkata&appid=5221e06a792c488608a995e0ace017ac";
    const [result, setResult] = useState(null);

    useEffect(() => {
      fetc(url).then((response) => {
        setResult(response.data);
        // axios returns API response body in .data
      });
    }, []); // second param [] is a list of dependency to watch and run useEffect

    return (
      <div>
        <p>{result === null ? "loading" : result}</p>
      </div>
    );
    
}
