import React, { Component } from "react";

// https://developer.mozilla.org/en-US/docs/Web/API/WindowOrWorkerGlobalScope/fetch
// https://swapi.co/
// https://medium.com/javascript-scene/master-the-javascript-interview-what-is-a-promise-27fc71e77261

class Weather extends Component {
  constructor() {
    super();
    this.state = {
      loading: false,
        character: {},
    };
  }

  componentDidMount() {
    this.setState({ loading: true });
    fetch(
      "http://api.openweathermap.org/data/2.5/forecast?id=524901&appid=a9dac8d426c074d0f1f8c06ec84d66c8"
    )
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          loading: false,
          character: data,
        });
      });
  }

  render() {
    const text = this.state.loading ? "loading..." : this.state.character.list;
    return (
      <div>
        <p>{text}</p>
      </div>
    );
  }
}

export default Weather;
