import React, { Component } from "react";
import WeatherData from './WeatherData';
// https://developer.mozilla.org/en-US/docs/Web/API/WindowOrWorkerGlobalScope/fetch
// https://swapi.co/
// https://medium.com/javascript-scene/master-the-javascript-interview-what-is-a-promise-27fc71e77261

class Weather extends Component {
  constructor() {
    super();
    this.state = {
      loading: true,
        character: {},
    };
  }

  componentDidMount() {
    this.setState({ loading: true });
    fetch(
      "http://api.openweathermap.org/data/2.5/forecast?lat=35&lon=139&appid=a9dac8d426c074d0f1f8c06ec84d66c8"
    )
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          loading: false,
          character: data,
        });
        //console.log(data);
        
        // let listArray = data.list;
        
        // console.log(listArray[0]);
      });
  }

  render() {
    
    //console.log(this.state);
    const text = this.state.loading ? "loading..." : "Daily Weather Forecast";
    let listArray = []
    if (this.state.loading == false) {
      for (var i = 0; i < 40; i += 5) {
        listArray.push(this.state.character.list[i]);
      }
      console.log(listArray);
    }
    let forecastList = listArray.map((data) => (
      <div className="col-md-3">
        <WeatherData date={data.dt_txt} tempMin={data.main.temp_min} tempMax={data.main.texp_max} description={ data.weather[0].description}/>
          
          
        </div>
      
    ));  

    return (
      
      <div className="row">
        
         {forecastLIst}
        
      </div>
    );
  }
}

export default Weather;
